import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';
import FavComponent from '../favorite/favorites';

export default function Favorites(props) {

  const [jobList, setJobList] = useState({
    datas: []
  });

  const [idList, setIdList] = useState([]);

  useEffect(() => {
    axios.get('/api/users/whoIsLoggedIn')
      .then(response1 => {
        console.log("api request")
        if (response1.data !== '') {
          // setJobList([]);
          // console.log("username:", response1.data)
          var idUrl = 'api/users/findFavorite/' + response1.data;
          axios.get(idUrl)
            .then(response2 => {
              console.log("id list:", response2.data)
              setIdList(response2.data);
              // console.log("fav list:", response.data)
              // var jobArray=[];
              // var idslist = response2.data;
              // var jobarr=[];
              // idslist.map((index, item) => {
              //   var jobUrl = 'api/jobs/findId/' + index;
              //   axios.get(jobUrl)
              //     .then(response3 => {
              //       jobarr.push(response3.data)
              //       setJobList({
              //           ...jobList,
              //           datas:response3.data
              //         });
            })
            .catch(error1 => {
              console.log(error1);
            })

            // console.log(jobarr)
            // setJobList({
            //   ...jobList,
            //   datas:jobarr
            // });


            .catch(error2 => {
              console.log(error2)
            });
        }
      })
      .catch(error3 => {
        console.log(error3)
      });
    // console.log("Joblist:", jobList.datas)
  }, []);

  return <div>
    <div className="searchResult">
      <h3>Your Favorites</h3>
      <div className='jobcard'>
        {idList.map(jobid => {
          // console.log("search"+item[0])
          var jobUrl = 'api/jobs/findId/' + jobid;
          axios.get(jobUrl)
            .then(response => {
              var jsontext = JSON.stringify(response.data);
              console.log(typeof response.data)
              return (
                <li key={jobid}>
                  <JobCard jobInfo={response.data} />
                </li>
              );
            })
            .catch(error => console.log(error));
        })}
      </div>
    </div>
    <Pagination />
  </div>

}
