import React, { Component, useEffect, useState } from "react";
import './jobDetail.css';
import jsonData from './testdata.json';
import { Link } from "react-router-dom";
import axios from "axios";

export default function JobDetail() {
    const currurl = window.location.pathname;
    const path = currurl.split('/');
    const id = path[path.length - 1];

    const [jobIfo, setJobInfo] = useState({
        jobid: '',
        job: '',
        user: '',
        company: '',
        location: '',
        description: '',
        email: '',
        website: ''
    });

    const [jobData, setJob] = useState({
        datas: []
    });

    const [userLog, setLog] = useState('');
    const [allFavorites, setAllFavorites] = useState([]);
    const [isFav, setFav] = useState(false);

    async function getLoggedUser() {
        const resUser = await axios.get('/api/users/whoIsLoggedIn');
        return resUser;
    }
    getLoggedUser.then(resUser => {
        if (resUser.data !== '') {
            //console.log(jobData.datas)
            setLog(resUser.data);
            setJobInfo({
                ...jobIfo,
                user: resUser.data,

            })
            axios.get('/api/users/findFavorite/' + resUser.data)
                .then(response => {
                    setAllFavorites(response.data)
                    response.data.some(jobid => {
                        if (jobid === id) {
                            setFav(true);
                            return true
                        }
                    })
                })
                .catch(error => console.error(error));
        }
    })

    useEffect(getLoggedUser,[]);

    async function getJobDetail() {
        var url = '/api/jobs/findId/' + id;
        axios.get(url)
            .then(response => {
                var arr = response.data[0];
