            })
            .catch(error => {
                console.log(error)
            });

            // var whoLogged = await axios.get('/api/users/whoIsLoggedIn');
            // whoLogged.then(response => {
                // if (response.data !== '') {
                //     //console.log(jobData.datas)
                //     setLog(response.data);
                //     setJobInfo({
                //         ...jobIfo,
                //         user: response.data,

                //     })
                //     axios.get('/api/users/findFavorite/' + response.data)
                //         .then(response => {
                //             setAllFavorites(response.data)
                //             response.data.some(jobid => {
                //                 if (jobid === id) {
                //                     setFav(true);
                //                     return true
                //                 }
                //             })
                //         })
                //         .catch(error => console.error(error));
                // }

            // })
            // .catch(error => {
            //     console.log(error)
            // });


            axios.get(url)
            .then(response => {
                // const job = response.data[0].job;
                setJobInfo({
                    ...jobIfo,
                    // job: job,
                    jobid: id,
                    job: response.data[0].job,
                    company: response.data[0].company,
                    location: response.data[0].location,
                    description: response.data[0].description,
                    email: response.data[0].email,
                    website: response.data[0].website
                })
                // console.log(job);
            })
            .catch(error => {
                console.log(error)
            });





    }


    useEffect(getJobDetail, []);

    const handleFavorite = () => {
        // setJobInfo({
        //     ...jobIfo,
        //     job: jobData.datas.job,
        //     company: jobData.datas.company,
        //     location: jobData.datas.location,
        //     description: jobData.datas.description,
        //     email: jobData.datas.email,
        //     website: jobData.datas.website
        console.log(jobIfo);

        // })
        if (isFav === true) {
            var urlUser = '/api/users/unfavorite';
            var urlFav = '/api/favorites/delete/' + jobData.datas._id + '/' + userLog;
            // axios.delete(urlFav)
            //     .then(response => {
            //         console.log(response)
            //     })
            //     .catch(error => console.error(error));
        }
        else {
            var urlUser = '/api/users/favorite';
            var urlFav = '/api/favorites/createFav'
        }


        //     axios.post(urlFav, jobIfo)
        //         .then(response => {
        //             console.log(response)
        //         })
        //         .catch(error => console.error(error));
        // }
        // axios.put(urlUser, {
        //     id: id
        // })
        //     .then(response => {
        //         setFav(!isFav);
        //         console.log(response)
        //     })
        //     .catch(error => console.error(error));
    }

    const handleDelete = () => {
        var url = '/api/jobs/delete/' + id;
        axios.delete(url)
            .then(response => {
                window.location.href = '/';
                console.log(response)
            })
            .catch(error => console.error(error));
    }

    const handleEdit = () => {
        window.location.href = '/editjob/' + id;
    }


    return <div>
        <div className="outer">
            <div className="inner">
                {/* <form> */}
                <div className="mb-3 row">
                    {/* <label for="staticTitle" className="col-sm-12 col-form-label">Job Title:  {jobData.datas.job}</label> */}
                    <label for="staticTitle" className="col-sm-12 col-form-label">Job Title:  {userLog}</label>

                </div>
                <div className="mb-3 row">
                    <label for="staticCompany" className="col-sm-12 col-form-label">Company:  {jobData.datas.company}</label>
                </div>
                <div className="mb-3 row">
