import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';
import helper from '../../helper';


export default function Favorites(props) {
  const [idList, setIdList] = useState({
    ids: []
  });
  const [userLog, setUserLog] = useState({
    user: '',
    isLog: false
  })
  useEffect(() => {
    axios.get('/api/users/whoIsLoggedIn')
      .then(response => {
        console.log(response.data);
        if (response.data !== '') {
          setUserLog({
            ...userLog,
            user: response.data,
            isLog: true
          });
        }
      })
      .catch(error => {
        console.log(error)
      });

  }, []);
  if (userLog.isLog === true) {
    var idUrl = 'api/users/findFavorite/' + userLog.user;
    axios.get(idUrl)
      .then(response => {
        setIdList({
          ...idList,
          ids: response.data
        });
        console.log(helper.username);
        console.log(idList.ids)
        return <div>
          <div className="searchResult">
            <h3>Your favorites</h3>
            <div className='jobcard'>
              {idList.ids.map((item, index) => {
                var jobUrl = 'api/jobs/findId/' + item;
                axios.get(jobUrl)
                  .then(response => {
                    return (
                      <li key={index}>
                        <JobCard jobInfo={response.data} />
                      </li>
                    );
                  })
                  .catch(error => {
                    console.log(error);
                  })
              })}

              {/* <JobCard />
                    <JobCard />
                    <JobCard />
                    <JobCard /> */}
            </div>
          </div>
          <Pagination />
        </div>
      })
      .catch(error => {
        console.log(error)
      });
  }
  else {
    // alert("Please log in!")
    alert(userLog.isLog);
    // window.location.href = '/';
    return <div></div>
  }


}
