import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';
import helper from '../../helper';


export default function Favorites(props) {
  const [idList, setIdList] = useState({
    ids: []
  });

  const [isVisit, setVisit] = useState({
    whoIsLoggedIn: false,
    findFavorite: false,
  });

  const [userLog, setUserLog] = useState({
    user: '',
    isLog: false
  })
  if (isVisit.whoIsLoggedIn === false) {
    axios.get('/api/users/whoIsLoggedIn')
      .then(response => {
        console.log(response.data);
        if (response.data !== '') {
          setUserLog({
            ...userLog,
            user: response.data,
            isLog: true
          });
          setVisit({
            ...isVisit,
            whoIsLoggedIn:true
          })
        }
      })
      .catch(error => {
        console.log(error)
      });
  }

  if (isVisit.findFavorite === false) {
    if (userLog.isLog === true) {
      var idUrl = 'api/users/findFavorite/' + userLog.user;
      axios.get(idUrl)
        .then(response => {
          setIdList({
            ...idList,
            ids: response.data
          });
          setVisit({
            ...isVisit,
            findFavorite:true
          })
          console.log(response.data);
          console.log(idList.ids)
        })
        .catch(error => {
          console.log(error)
        });
    }
  }


  return (
    <div>
      <div className="searchResult">
        <h3>Your favorites</h3>
        <div className='jobcard'>
          {idList.ids.map((item, index) => {
            var jobUrl = 'api/jobs/findId/' + item;
            axios.get(jobUrl)
              .then(response => {
                return (
                  <li key={index}>
                    <JobCard jobInfo={response.data} />
                  </li>
                );
              })
              .catch(error => {
                console.log(error);
              })
          })}

          {/* <JobCard />
                    <JobCard />
                    <JobCard />
                    <JobCard /> */}
        </div>
      </div>
      <Pagination />
    </div>
  );

}
