import React, { Component, useEffect, useState } from "react";
import './jobDetail.css';
import jsonData from './testdata.json';
import { Link } from "react-router-dom";
import axios from "axios";

export default function JobDetail() {
    const currurl = window.location.pathname;
    const path = currurl.split('/');
    const id = path[path.length - 1];

    const [jobIfo, setJobInfo] = useState({
        jobid: '',
        job: '',
        user: '',
        company: '',
        location: '',
        description: '',
        email: '',
        website: ''
    });

    const [jobData, setJob] = useState({
        datas: []
    });

    const [userLog, setLog] = useState('');
    const [allFavorites, setAllFavorites] = useState([]);
    const [isFav, setFav] = useState(false);

    async function getLoggedUser() {
        const errorUser = await axios.get('/api/users/whoIsLoggedIn')
            .then(resUser => {
                if (resUser.data !== '') {
                    console.log(resUser.data)
                    setLog(resUser.data);
                    setJobInfo({
                        ...jobIfo,
                        user: resUser.data,

                    })
                    axios.get('/api/users/findFavorite/' + resUser.data)
                        .then(response => {
                            setAllFavorites(response.data)
                            response.data.some(jobid => {
                                if (jobid === id) {
                                    setFav(true);
                                    return true
                                }
                            })
                        })
                        .catch(error => console.error(error));
                }
            })
        return errorUser;
    }



    function getJobDetail() {
        var url = '/api/jobs/findId/' + id;
        axios.get(url)
            .then(response => {
                console.log(userLog)
                var arr = response.data[0];
                setJob({
                    ...jobData,
                    datas: response.data[0]
                })
            })
            .catch(error => {
                console.log(error)
            });

        getLoggedUser().catch(errorUser => console.error(errorUser));

        axios.get(url)
        .then(response => {
            // const job = response.data[0].job;
            setJobInfo({
                ...jobIfo,
                // job: job,
                jobid: id,
                job: response.data[0].job,
                company: response.data[0].company,
                location: response.data[0].location,
                description: response.data[0].description,
                email: response.data[0].email,
                website: response.data[0].website
            })
            // console.log(job);
        })
        .catch(error => {
            console.log(error)
        });
    }


