}

const handleDelete = () => {
    var url = '/api/jobs/delete/' + id;
    axios.delete(url)
        .then(response => {
            window.location.href = '/';
            console.log(response)
        })
        .catch(error => console.error(error));
}

const handleEdit = () => {
    window.location.href = '/editjob/' + id;
}


