import React, { useState, useEffect } from 'react';
import '../../App.css';
import JobDetail from '../jobDisplay/JobDetail';
import '../jobDisplay/jobDetail.css';
import axios from "axios";


export default function Details() {
  // return <JobDetail />;
  const currurl = window.location.pathname;
  const path = currurl.split('/');
  const id = path[path.length - 1];
  console.log("location:", path)

  const [jobData, setJob] = useState({
    datas: []
  });

  useEffect(() => {
    var url = '/api/jobs/findId/' + id;
    axios.get(url)
      .then(response => {
        setJob({
          ...jobData,
          datas: response.data
        })
        console.log(jobData.datas)
      })
      .catch(error => {
        console.log(error)

      });

  }, []);
  return <div>
    <div className="outer">
      <div className="inner">
        {jobData.datas}
        {/* <form> */}
        <div className="mb-3 row">
          <label for="staticTitle" className="col-sm-12 col-form-label">Job Title:  {jobData.datas.job}</label>
        </div>
        <div className="mb-3 row">
          <label for="staticCompany" className="col-sm-12 col-form-label">Company:  {jobData.company}</label>
        </div>
        <div className="mb-3 row">
          <label for="staticLocation" className="col-sm-12 col-form-label">Location:  {jobData.location}</label>
        </div>
        {/* <div className="mb-3 row">
                            <label for="staticDate" className="col-sm-12 col-form-label">Post Date:  {jsonData.date}</label>
                        </div> */}
        <div className="mb-3 row">
          <label for="staticDescription" className="col-sm-12 col-form-label">Description:  {jobData.description}</label>
        </div>
        <div className="mb-3 row">
          <label for="staticEmail" className="col-sm-12 col-form-label">Contact Email:  {jobData.email}</label>
        </div>
        <div className="mb-3 row">
          <label for="staticWeb" className="col-sm-12 col-form-label">Webpage:  {jobData.website}</label>
        </div>
        <div className="mb-3 row">
          {/* <div className="col-sm-offset-2 col-sm-2">
                            <button type="button" className={favButtonName} >{favContent}</button>
                        </div> */}
          <div className="col-sm-offset-2 col-sm-2">
            <button type="button" className="btn btn-info btn-lg" >Edit</button>
          </div>
        </div>
        {/* </form> */}
      </div>
    </div >
  </div>
}
