import axios from "axios";
import JobCard from "../jobDisplay/jobCard";


export default function JobIdComponent(props) {
    const [allFavIds, setAllFavIds] = useState(props.idList);
    // const user = props.userName;
    const [allJobs, setAllJobs] = useState([]);



    function findAllPokemon() {
        allFavIds.map(id => {
            axios.get('http://localhost:8000/api/users/findId/' + id)
            .then(response => {
                setAllJobs([...allJobs,response.data])
            })
            .catch(error => console.error(error));
        })

    }

    useEffect(findAllPokemon, []);
        return (
