import React, { useState } from "react";
import axios from "axios";
import './components/register/signin.css';

export default (props) => {
    const [userData, setUserData] = useState({
        password: '',
        username: '',
    });
    // render() {
        return (
            <div className="outer">
                <div className="inner">
                    <form>
                        <h3>Register</h3>

                        <div className="form-group">
                            <label>username</label>
                            <input type="text" className="form-control" placeholder="Enter username" value={userData.username} onChange={(e) => {
                const username = e.target.value;
                setUserData({
                    ...userData,
                    username: username
                })
            }}/>
                        </div>

                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" className="form-control" placeholder="Enter password" value={userData.password} onChange={(e) => {
                const password = e.target.value;
                setUserData({
                    ...userData,
                    password: password
                })
            }}/>
                        </div>

                        <div className="form-group">
                            <label>Confirm Password</label>
                            <input type="password" className="form-control" placeholder="Comfirm password" />
                        </div>

                        <button className="btn btn-dark btn-lg btn-block" onClick={() => {
                    axios.post('/api/users/register', userData)
                        .then(response => {
                            // console.log(response);
                            alert("register success");
                        })
                        .catch(error => {
                            // console.log(error);
                            alert("the user has existed.");
                        });
                }}>Register</button>
                        <p className="forgot-password text-right">
                            Already registered? <a href="/sign-in">Log in</a>
                        </p>
                    </form>
                </div>
            </div>
        );
    }
