import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';
import helper from '../../helper';
import JobIdComponent from '../favorite/getJobIdComponent';

export default function FavoritesCopy() {

  const [allPokemon, setAllPokemon] = useState([]);
  const [userName, setUserName] = useState('');

    function findAllPokemon() {
        axios.get('/api/users/whoIsLoggedIn')
            .then(logRes => {
              setUserName(logRes.data)
              axios.get('/api/users/findFavorite/' + logRes.data)
              .then(idRes => {
                console.log("FavList Data: ",idRes.data)
                var idlist = idRes.data;
                var resArr = [];
                idlist.map(id => {
                    axios.get('/api/jobs/findId/' + id)
                    .then(jobRes => {
                        resArr.push(jobRes.data);
                        console.log(jobRes.data)
                    })
                    .catch(jobError => console.log(jobError))
                })
                setAllPokemon(resArr);
              })
              .catch(idError => console.log(idError))
            })
            .catch(logError => console.error(logError));
    }

    useEffect(findAllPokemon, []);
    return <div>
    <div className="searchResult">
        <h3>Your favorites copy</h3>
        <div className='jobcard'>
        {allPokemon.map((item, index) => {
                    console.log("search"+item)
                    return (
                        <li key={index}>
                            <JobCard jobInfo={item} />
                        </li>
                    );

                })}

            {/* <JobCard />
                <JobCard />
                <JobCard />
                <JobCard /> */}
        </div>
    </div>
    <Pagination />
</div>
}
