import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';

export default function SearchResult(props) {
    const currurl = window.location.pathname;
    const path = currurl.split('/');
    const keyword = path[path.length - 1];
    // export default class SearchResult extends Component {
    // constructor(props) {
    //     super(props);
    // }
    // return <h1 className='sign-up'>LIKE & SUBSCRIBE</h1>;
    // render() {
    // const {keyword} = props.location.state;
    console.log("location:", path)
    // const keyword = 'sde';)
    const [jobData, setJob] = useState({
        datas: []
    });

    useEffect(() => {
        if (keyword === 'alljobs') {
            var url = '/api/jobs/findAll';
            axios.get(url)
                .then(response => {
                    setJob({
                        ...jobData,
                        datas: response.data
                    })
                    console.log(jobData.datas)
                })
                .catch(error => {
                    console.log(error)
                });
        }
        else {
            var url = '/api/jobs/findJob/' + keyword;
            axios.get(url)
                .then(response => {
                    setJob({
                        ...jobData,
                        datas: response.data
                    })
                })
                .catch(error => {
                    console.log(error)

                });
        }
    }, []);

    return <div>
        <div className="searchResult">
            <h3>Search Result for "{keyword}"</h3>
            <div className='jobcard'>
                {jobData.datas.map((item, index) => {
                    console.log("search"+item)
                    return (
                        <li key={index}>
                            <JobCard jobInfo={item} />
                        </li>
                    );

                })}

                {/* <JobCard />
                    <JobCard />
                    <JobCard />
                    <JobCard /> */}
            </div>
        </div>
        <Pagination />
    </div>
}
