import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';
import helper from '../../helper';


export default function Favorites(props) {

  const [jobList, setJobList] = useState([]);

  useEffect(() => {
    axios.get('/api/users/whoIsLoggedIn')
      .then(response => {
        console.log("api request")
        if (response.data !== '') {
          setJobList([]);
          // console.log("username:", response.data)
          var idUrl = 'api/users/findFavorite/' + response.data;
          axios.get(idUrl)
            .then(response => {
              // console.log("fav list:", response.data)
              // var jobArray=[];
              var idslist = response.data;
              var jobarr=[];
              idslist.map((index, item) => {
                console.log("each id:",index)
                var jobUrl = 'api/jobs/findId/' + index;
                axios.get(jobUrl)
                  .then(res => {
                    // console.log("jobs:", item, ",", res.data)
                    // jobArray.push(res.data);
                    // setJobList([...jobList, res.data]);
                    jobarr.push(res.data)
                    console.log("Joblist:", jobarr)

                  })
                  .catch(error => {
                    console.log(error);
                  })
              })
              setJobList(jobarr);

            })
            .catch(error => {
              console.log(error)
            });
        }
      })
      .catch(error => {
        console.log(error)
      });
  }, []);

  return <div>
   <div className="searchResult">
            <h3>Your Favorites</h3>
            <div className='jobcard'>
                {jobList.map((item, index) => {
                    console.log("search"+item)
                    return (
                        <li key={index}>
                            <JobCard jobInfo={item} />
                        </li>
                    );

                })}

                {/* <JobCard />
                    <JobCard />
                    <JobCard />
                    <JobCard /> */}
            </div>
        </div>
        <Pagination />
  </div>

}
