import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';
import FavComponent from '../favorite/favorites';
import helper from '../../helper';

export default function Favorites(props) {

  const [jobList, setJobList] = useState(null);

  const [idList, setIdList] = useState();

  function getFavoritesIds() {
    console.log("logging user:", helper.username)
    if (helper.username) {
      var idUrl = '/api/users/findFavorite/' + helper.username;
      axios.get(idUrl)
        .then(response => {
          console.log("id list:", response.data)
          setIdList(response.data);
        })
        .catch(error => {
          console.log(error);
        })
    }
  }

  function getFavoritesJobs() {
    idList.map(jobid => {
      var jobUrl = '/api/jobs/findId/' + jobid;
      axios.get(jobUrl)
        .then(response => {
          var temp = response.data;
          console.log(temp[0]._id)
          setJobList([...jobList, temp[0]]);
        })
        .catch(error => console.log(error));
    })
  }

  useEffect(getFavoritesIds, []);
  useEffect(getFavoritesJobs, []);



