  // }, []);

  const jobCardComponent = jobList ? jobList.map((item, index) => {
    console.log("search:" + item)
    return (
      <li key={index}>
        <JobCard jobInfo={item} />
      </li>
    );

  }) : "No favorite job."
  // });

  return (
    // return (<div>
