import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import CusDropdown from './CusDropdown';
import helper from '../../helper';

function Navbar() {
  const [click, setClick] = useState(false);
  const [cus_dropdown, setCusDropdown] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setCusDropdown(false);
    } else {
      setCusDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setCusDropdown(false);
    } else {
      setCusDropdown(false);
    }
  };

  if (helper.username === '') {
    var button = <li className='nav-item'>
      <Button />
    </li>
  }
  else{
    button = <li
    className='nav-item'
    onMouseEnter={onMouseEnter}
    onMouseLeave={onMouseLeave}
  >
    <Link
      to='/services'
      className='nav-links'
      onClick={closeMobileMenu}
    >
      {helper.username} <i className='fas fa-caret-down' />
    </Link>
    {cus_dropdown && <CusDropdown />}

  </li>
  }

  return (
    <>
      <nav className='navbar'>
        <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          Job Hunter
          <i className='fab fa-firstdraft' />
        </Link>
        <div className='menu-icon' onClick={handleClick}>
          <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
          <li className='nav-item'>
            <Link to='/' className='nav-links' onClick={closeMobileMenu}>
              Home
            </Link>
          </li>

          <li className='nav-item'>
            <Link
              to='/favorites'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Favorites
            </Link>
          </li>
          <li className='nav-item'>
            <Link
              to='/contact-us'
              className='nav-links'
              onClick={closeMobileMenu}
            >
              Contact Us
            </Link>
          </li>
          {button}
        </ul>

      </nav>
    </>
  );
}

export default Navbar;
