import React, { useEffect, useState } from 'react';
import '../../App.css';
import JobCard from '../jobDisplay/jobCard.js';
import Pagination from '../jobDisplay/Pagination';
import axios from 'axios';
import helper from '../../helper';


export default function Favorites(props) {

  const [jobList, setJobList] = useState([]);

  const [userLog, setUserLog] = useState({
    user: '',
    isLog: false
  })

  useEffect(() => {
    axios.get('/api/users/whoIsLoggedIn')
      .then(response => {
        console.log("api request")
        if (response.data !== '') {
          // setJobList([]);
          // setUserLog({
          //   ...userLog,
          //   user: response.data,
          //   isLog: true
          // });
          console.log("username:",response.data)
          var idUrl = 'api/users/findFavorite/' + response.data;
          axios.get(idUrl)
            .then(response => {
              console.log("fav list:",response.data)
              var jobArray=[];
              response.data.map(jobid => {
                var jobUrl = 'api/jobs/findId/' + jobid;
                axios.get(jobUrl)
                  .then(res => {
                    console.log("jobs:",jobid,",",res.data)
                    jobArray.push(res.data);
                  })
                  .catch(error => {
                    console.log(error);
                  })
              })
              setJobList([...jobList, jobArray]);
            })
            .catch(error => {
              console.log(error)
            });
        }
      })
      .catch(error => {
        console.log(error)
      });
      console.log("Joblist:",jobList)
  }, []);

  return <div>
    <div className="searchResult">
      <h3>Your Favorites</h3>
      <div className='jobcard'>
        {jobList.map(items => {
          return (
            <li>
              {items.job}
              <JobCard jobInfo={items} />
            </li>
          );

        })}
      </div>
    </div>
    <Pagination />
  </div>

}
