import React, { Component, useEffect, useState } from "react";
import './jobDetail.css';
import jsonData from './testdata.json';
import { Link } from "react-router-dom";
import axios from "axios";

export default function JobDetail(props)  {
    const currurl = window.location.pathname;
    const path = currurl.split('/');
    const id = path[path.length - 1];
    const [jobData, setJobData] = useState({
        jobDetail:[]
    });
    const [userLog, setUserLog] = useState({
        user: '',
        isLog: false
      })

    console.log('/api/jobs/findId/' + id);

    useEffect(() => {
        axios.get('/api/jobs/findId/' + id)
            .then(response => {
                setJobData({
                    ...jobData,
                    jobDetail:response.data
                })
                console.log(response.data)

            })
            .catch(error => {
                console.log(error)
                alert("get job fail")

            });
    }, []);

    useEffect(() => {
        axios.get('/api/users/whoIsLoggedIn')
      .then(response => {
        console.log("api request")
        // alert(response.request.responseURL)
        if(response.data!==''){
          setUserLog({
              ...userLog,
              user: response.data,
              isLog:true
          });
          console.log(userLog.user)
        }
    })
    .catch(error => console.log(error))
    },[]);


    var favButtonName = "btn btn-danger btn-lg";
    var favContent = "Favorite";
    console.log(jobData.jobDetail + "!!!!");
    // if (jobData.jobDetail.favorites.length === 0) {
    //     favButtonName = "btn btn-secondary btn-lg";
    //     favContent = "Unfavorite";
    // }
    return (
        <div className="outer">
            <div className="inner">
                <form>
                    <div className="mb-3 row">
                        <label for="staticTitle" className="col-sm-12 col-form-label">Job Title:  {jobData.job}</label>
                    </div>
                    <div className="mb-3 row">
                        <label for="staticCompany" className="col-sm-12 col-form-label">Company:  {jobData.jobDetail.company}</label>
                    </div>
                    <div className="mb-3 row">
                        <label for="staticLocation" className="col-sm-12 col-form-label">Location:  {jobData.jobDetail.location}</label>
                    </div>
                    {/* <div className="mb-3 row">
                            <label for="staticDate" className="col-sm-12 col-form-label">Post Date:  {jsonData.date}</label>
                        </div> */}
                    <div className="mb-3 row">
                        <label for="staticDescription" className="col-sm-12 col-form-label">Description:  {jobData.jobDetail.description}</label>
                    </div>
                    <div className="mb-3 row">
                        <label for="staticEmail" className="col-sm-12 col-form-label">Contact Email:  {jobData.jobDetail.email}</label>
                    </div>
                    <div className="mb-3 row">
                        <label for="staticWeb" className="col-sm-12 col-form-label">Webpage:  {jobData.jobDetail.website}</label>
                    </div>
                    <div className="mb-3 row">
                        <div className="col-sm-offset-2 col-sm-2">
                            <button type="button" className={favButtonName} >{favContent}</button>
                        </div>
                        <div className="col-sm-offset-2 col-sm-2">
                            <button type="button" className="btn btn-info btn-lg" >Edit</button>
                        </div>
                    </div>
                </form>
            </div>
        </div >
    );

}
